/** Type guards to define whether a value is of a specific type */
export declare const isClient: boolean;
export declare const isBoolean: (val: unknown) => val is boolean;
export declare const isFunction: <T extends (arg0: unknown) => unknown>(val: unknown) => val is T;
export declare const isNumber: (val: unknown) => val is number;
export declare const isString: (val: unknown) => val is string;
export declare const isObject: (val: unknown) => val is object;
export declare const isWindow: (val: unknown) => val is Window;
export declare const now: () => number;
export declare const timestamp: () => number;
export declare const sleep: (ms?: number) => Promise<unknown>;
export declare const rand: (min: number, max: number) => number;
