import { searchForKeys, searchForValues } from './utils.ts';
const defaultOpts = {
    searchFor: 'keys'
};
export function useDeepSearch(object, opts = defaultOpts) {
    const mergedOpts = { ...defaultOpts, ...opts };
    const { searchFor } = mergedOpts;
    if (searchFor === 'keys') {
        return searchForKeys(object);
    }
}
