import type { ConfigurableWindow } from '../../shared/utilities/index.js';
import type { Pauseable } from '../../shared/utilities/types';
export interface RafFnOptions extends ConfigurableWindow {
    /**
     * Start the requestAnimationFrame loop immediately on creation
     *
     * @default true
     */
    immediate?: boolean;
}
/**
 * Call function on every `requestAnimationFrame`. With controls of pausing and resuming.
 *
 * ```tsx
 * <script>
 * 	let count = 0;
 * 	const { pause, resume } = useRafFn(() => {
 * 		count++
 *     })
 * </script>
 *
 * Count: {count}
 * <button on:click={() => pause()}>Pause</button>
 * <button on:click={() => resume()}>Resume</button>
 * ```
 *
 * @param fn callback function
 * @param options controls whether the callback is triggered immediately
 * @see https://svelteui.org/utilities/raf-fn
 */
export declare function useRafFn(fn: () => void, options?: RafFnOptions): Pauseable;
