<script>import ActionIcon from '../ActionIcon.svelte';
import CloseIcon from './CloseIcon.svelte';
import { createEventForwarder, useActions } from '../../../internal';
import { get_current_component } from 'svelte/internal';
export let use = [], element = undefined, className = '', override = {}, iconSize = 'md', root = 'button', color = 'gray', variant = 'hover', size = 'md', radius = 'sm', loaderProps = {
    size: 'xs',
    color: 'gray',
    variant: 'circle'
}, loading = false, disabled = false, href = '', external = false;
export { className as class };
/** An action that forwards inner dom node events from parent component */
const forwardEvents = createEventForwarder(get_current_component());
const iconSizes = {
    xs: 12,
    sm: 14,
    md: 16,
    lg: 20,
    xl: 24
};
</script>

<!--
@component

CloseButton is a premade ActionIcon with close icon

@see https://svelteui.org/core/action-icon
@example
    ```tsx
    <CloseButton /> // standard CloseButton
    ```
-->

<ActionIcon
	bind:element
	class={className}
	use={[forwardEvents, [useActions, use]]}
	{override}
	{root}
	{color}
	{variant}
	{size}
	{radius}
	{loaderProps}
	{loading}
	{disabled}
	{href}
	{external}
	{...$$restProps}
>
	<CloseIcon width={iconSizes[iconSize]} height={iconSizes[iconSize]} />
</ActionIcon>
