<script context="module">export {};
</script>

<script>import useStyles from './HorizontalSection.styles';
import { Box } from '../../Box';
import { globalCss } from '../../../styles';
import { getSortedBreakpoints, theme as appShellTheme } from './get-sorted-breakpoints/get-sorted-breakpoints';
export let use = [], element = undefined, className = '', override = {}, width = undefined, height = undefined, fixed = false, position = {}, hiddenBreakpoint = 'md', hidden = false, zIndex = 100, section;
export { className as class };
const breakpoints = getSortedBreakpoints(width, appShellTheme).reduce((acc, [breakpoint, breakpointSize]) => {
    acc[`@media (min-width: ${breakpoint + 1}px)`] = {
        [`--svelteui-${section}-width`]: `${breakpointSize}px`
    };
    return acc;
}, {});
$: ({ cx, classes, getStyles } = useStyles({
    fixed,
    height,
    hiddenBreakpoint,
    position,
    width,
    zIndex,
    section,
    hidden
}, { name: 'HorizontalSection' }));
const injectRoot = globalCss({
    ':root': {
        [`--svelteui-${section}-width`]: width?.base ? `${width.base}px` : '0px',
        ...breakpoints
    }
});
injectRoot();
</script>

<Box
	{use}
	bind:element
	root={section === 'navbar' ? 'nav' : 'aside'}
	class={cx(className, classes.root, getStyles({ css: override }))}
	{...$$restProps}
>
	<slot />
</Box>
