<script>import useStyles from './VerticalSection.styles';
import { Box } from '../../Box';
import { globalCss } from '../../../styles';
export let use = [], element = undefined, className = '', override = {}, height = undefined, fixed = false, position = {}, zIndex = 100, section;
export { className as class };
$: ({ cx, classes, getStyles } = useStyles({
    borderPosition: section === 'header' ? 'bottom' : 'top',
    fixed,
    height,
    position,
    zIndex
}, { name: 'VerticalSection' }));
const injectStyles = globalCss({
    ':root': {
        [`--svelteui-${section}-height`]: `${height}px`
    }
});
injectStyles();
</script>

<Box
	{use}
	bind:element
	root={section === 'header' ? 'nav' : 'footer'}
	class={cx(className, classes.root, getStyles({ css: override }))}
	{...$$restProps}
>
	<slot />
</Box>
