<script>import useStyles from './Checkbox.styles';
import { randomID } from '../../styles';
import { get_current_component } from 'svelte/internal';
import { createEventForwarder, useActions } from '../../internal';
import Box from '../Box/Box.svelte';
import ThemeIcon from '../ThemeIcon/ThemeIcon.svelte';
import CheckboxIcon from './CheckboxIcon.svelte';
export let use = [], element = undefined, className = '', override = {}, color = 'blue', id = randomID(), disabled = false, value = null, checked = false, indeterminate = false, label = null, radius = 'sm', size = 'md', name = '', transitionDuration = 100;
export { className as class };
/** An action that forwards inner dom node events from parent component */
const forwardEvents = createEventForwarder(get_current_component());
$: checked = indeterminate || checked;
$: ({ cx, classes, getStyles } = useStyles({ color, radius, size, transitionDuration }, { name: 'Checkbox' }));
</script>

<!--
@component

A checkbox input component using the theme styles with support for a label and indeterminate state.

@see https://svelteui.org/core/checkbox
@example
    ```svelte
    <Checkbox />
    <Checkbox size={'lg'} label={'Please accept this'} />
    <Checkbox undetermined />
    <Checkbox checked disabled />
    ```
-->

<Box
	bind:element
	class={cx(className, classes.root, getStyles({ css: override }))}
	{...$$restProps}
>
	<div class={classes.inner}>
		<input
			use:useActions={use}
			use:forwardEvents
			bind:checked
			class={classes.input}
			class:disabled
			type="checkbox"
			{disabled}
			{value}
			{id}
			{name}
		/>
		<ThemeIcon class={classes.iconWrapper} variant={null} {size}>
			<slot>
				<CheckboxIcon class={classes.icon} {indeterminate} />
			</slot>
		</ThemeIcon>
	</div>
	{#if label}
		<label class={classes.label} for={id}>
			{label}
		</label>
	{/if}
</Box>
