<script>import { fade, slide } from 'svelte/transition';
import { createEventForwarder } from '../../internal';
import { get_current_component } from 'svelte/internal';
import { Box } from '../Box';
export let use = [], element = undefined, className = '', override = {}, open, animateOpacity = true, transitionDuration = 200, slideTransitionOptions = { duration: transitionDuration };
export { className as class };
const forwardEvents = createEventForwarder(get_current_component());
</script>

<!--
@component

A section that collapses with animation, occupying no space on the page.

@see https://svelteui.org/core/collapse
@example
    ```svelte
    <Collapse open>Content that collapses</Collapse>
    ```
-->
<Box class={className} bind:element css={override} {use} {...$$restProps}>
	{#if open}
		{#if animateOpacity}
			<div transition:fade={{ duration: transitionDuration }}>
				<div transition:slide={slideTransitionOptions} use:forwardEvents>
					<slot />
				</div>
			</div>
		{:else}
			<div transition:slide={slideTransitionOptions} use:forwardEvents>
				<slot />
			</div>
		{/if}
	{/if}
</Box>
