<script>import useStyles from './Container.styles';
import Box from '../Box/Box.svelte';
export let use = [], element = undefined, className = '', override = {}, size = 'md', fluid = false, sizes = {
    xs: 540,
    sm: 720,
    md: 960,
    lg: 1140,
    xl: 1320
};
export { className as class };
$: ({ cx, classes, getStyles } = useStyles({ fluid, size, sizes }, { name: 'Container' }));
</script>

<!--
@component

Center content horizontally with predefined max-width

@see https://svelteui.org/core/container
@example
    ```svelte
    <Container>
        Default container
    </Container>

    <Container size="xs" px="xs">
        xs container with xs horizontal padding
    </Container>

    <Container size={200} px={0}>
        200px container with 0px horizontal padding
    </Container>
    ```
-->
<Box
	bind:element
	{use}
	class={cx(className, classes.root, getStyles({ css: override }))}
	{...$$restProps}
>
	<slot />
</Box>
