<script>import useStyles from './Flex.styles';
import Box from '../Box/Box.svelte';
export let use = [], element = undefined, className = '', override = {}, gap = undefined, rowGap = undefined, columnGap = undefined, align = undefined, justify = undefined, wrap = undefined, direction = undefined;
export { className as class };
$: ({ cx, classes, getStyles } = useStyles({
    gap,
    rowGap,
    columnGap,
    align,
    justify,
    wrap,
    direction
}, { name: 'Flex' }));
</script>

<!--
@component

CSS flexbox component.

@see https://svelteui.org/core/flex
@example
    ```svelte
  <Flex>
		<Button variant="outline">1</Button>
		<Button variant="outline">2</Button>
		<Button variant="outline">3</Button>
	</Flex>
    ```
-->

<Box
	bind:element
	{use}
	class={cx(className, classes.root, getStyles({ css: override }))}
	{...$$restProps}
>
	<slot />
</Box>
