<script>import { getContext } from 'svelte';
import { Box } from '../../Box';
import useStyles from './Col.styles';
export let use = [], element = undefined, className = '', override = {}, span = undefined, offset = 0, offsetXs = 0, offsetSm = 0, offsetMd = 0, offsetLg = 0, offsetXl = 0, xs = undefined, sm = undefined, md = undefined, lg = undefined, xl = undefined;
export { className as class };
// retrieves the reactive context so that Col has access
// to the Grid cols, grow and spacing parameters
const state = getContext('Grid');
$: ({ cols, grow, spacing } = $state);
function isSpanValid(span) {
    return typeof span === 'number' && span > 0 && span % 1 === 0;
}
$: _span = span || cols || 0;
$: valid = isSpanValid(_span) && _span <= cols;
$: ({ cx, classes, getStyles } = useStyles({
    span: _span,
    cols,
    grow,
    spacing,
    offset,
    offsetXs,
    offsetSm,
    offsetMd,
    offsetLg,
    offsetXl,
    xs,
    sm,
    md,
    lg,
    xl
}, { name: 'Col' }));
</script>

{#if valid}
	<Box
		bind:element
		{use}
		class={cx(className, classes.root, getStyles({ css: override }))}
		{...$$restProps}
	>
		<slot />
	</Box>
{/if}
