<script>import useStyles from './Group.styles';
import { onMount } from 'svelte';
import Box from '../Box/Box.svelte';
export let use = [], element = undefined, className = '', override = {}, position = 'left', noWrap = false, grow = false, spacing = 'md', direction = 'row', align = 'center';
export { className as class };
/** The children being rendered */
let children;
/** can only get access to children at runtime */
$: onMount(() => {
    children = element.childElementCount;
});
$: ({ cx, classes, getStyles } = useStyles({
    align,
    children,
    direction,
    grow,
    noWrap,
    position,
    spacing
}, { name: 'Group' }));
</script>

<!--
@component

Compose elements and components in a vertical flex container.

@see https://svelteui.org/core/group
@example
    ```svelte
    <Group grow>
		<Button variant="outline">1</Button>
		<Button variant="outline">2</Button>
		<Button variant="outline">3</Button>
	</Group>
    ```
-->

<Box
	bind:element
	{use}
	class={cx(className, classes.root, getStyles({ css: override }))}
	{...$$restProps}
>
	<slot />
</Box>
