<script>import { get_current_component } from 'svelte/internal';
import { createEventForwarder, useActions } from '../../internal';
import Box from '../Box/Box.svelte';
import IconRenderer from '../IconRenderer/IconRenderer.svelte';
import useStyles from './Input.styles';
export let use = [], element = undefined, className = '', override = {}, root = 'input', icon = null, iconWidth = 36, iconProps = { size: 20, color: 'currentColor' }, showRightSection = $$slots.rightSection, rightSectionWidth = 36, rightSectionProps = {}, wrapperProps = {}, id = 'input-id', required = false, radius = 'sm', variant = 'default', disabled = false, size = 'sm', value = '', invalid = false, multiline = false, autocomplete = 'on', type = 'text', placeholder = undefined, autofocus = undefined, resize = 'none';
export { className as class };
/** An action that forwards inner dom node events from parent component */
const forwardEvents = createEventForwarder(get_current_component());
function castRoot() {
    return root;
}
function isInput(root) {
    return ['input', 'select', 'textarea', 'datalist'].includes(root);
}
let isHTMLElement = true;
let isComponent = false;
function onChange() {
    // the 'this' keyword in this case is the
    // HTML element provided in prop 'root'
    value = this.value;
}
function onInput(event) {
    if (event.target.type === 'checkbox') {
        value = event.target.checked;
    }
    else if (event.target.type === 'number' || event.target.type === 'range') {
        value = +event.target.value;
    }
    else {
        value = event.target.value;
    }
}
$: {
    isHTMLElement = root && typeof root === 'string';
    isComponent = root && typeof root === 'function';
}
$: ({ cx, classes, getStyles } = useStyles({
    icon,
    iconWidth,
    invalid,
    multiline,
    radius,
    rightSectionWidth,
    showRightSection,
    size,
    resize,
    variant
}, { name: 'Input' }));
</script>

<!--
@component
**DISCLAIMER: In most cases, you should not use Input component in your application. Input component is a base for other inputs and was not designed to be used directly.**

Base component to create custom inputs

@see https://svelteui.org/core/input
@example
    ```svelte
    <Input
      icon={Twitter}
      placeholder="Your twitter"
      rightSectionWidth={70}
      override={{ '& .rightSection': { pointerEvents: 'none' } }}
    >
		<Badge slot='rightSection' color="blue" variant="filled">
			new
		</Badge>
	<Input />
    ```
-->

<!-- svelte-ignore a11y-autofocus -->
<Box {...wrapperProps} class={cx(classes.root, getStyles({ css: override }))} {...$$restProps}>
	{#if icon}
		<div class={classes.icon}>
			<IconRenderer {icon} {iconProps} iconSize={16} />
		</div>
	{/if}
	{#if isHTMLElement && root === 'input'}
		<input
			{value}
			{type}
			bind:this={element}
			use:useActions={use}
			use:forwardEvents
			{required}
			{disabled}
			{id}
			{placeholder}
			{autocomplete}
			{autofocus}
			aria-invalid={invalid}
			class:withIcon={icon}
			class={cx(
				className,
				classes.input,
				{ [classes.disabled]: disabled, [classes.invalid]: invalid },
				classes[`${variant}Variant`] ?? {}
			)}
			{...$$restProps}
			on:input={onInput}
		/>
	{:else if isHTMLElement && isInput(String(root))}
		<!-- on:change needs to appear before use:forwardEvents so that the
   		ordering of the events is correct and the value is updated before propagation -->
		<!-- prettier-ignore -->
		<svelte:element
			this={castRoot()}
			bind:this={element}
			{value}
			{required}
			{disabled}
			{id}
			{placeholder}
			{autocomplete}
			{type}
			{autofocus}
			aria-invalid={invalid}
			class:disabled
			class:invalid
			class:withIcon={icon}
			class={cx(
				className,
				classes.input,
				{ [classes.disabled]: disabled, [classes.invalid]: invalid },
				classes[`${variant}Variant`] ?? {}
			)}
			on:change={onChange}
			use:useActions={use}
			use:forwardEvents
			{...$$restProps}
		>
			<slot />
		</svelte:element>
	{:else if isComponent && typeof root !== 'string'}
		<svelte:component
			this={root}
			bind:element
			bind:value
			use={[forwardEvents, [useActions, use]]}
			aria-invalid={invalid}
			class={cx(
				className,
				{
					[classes.disabled]: disabled,
					[classes.invalid]: invalid,
					[classes.withIcon]: icon
				},
				classes[`${variant}Variant`] ?? {}
			)}
			{disabled}
			{required}
			{id}
			{type}
			{autofocus}
			{...$$restProps}
		>
			<slot />
		</svelte:component>
	{/if}
	{#if showRightSection}
		<div {...rightSectionProps} class={classes.rightSection}>
			<slot name="rightSection" />
		</div>
	{/if}
</Box>
