<script>import useStyles from './InputWrapper.styles';
import Box from '../Box/Box.svelte';
import Text from '../Text/Text.svelte';
import LabelElement from './LabelElement.svelte';
export let use = [], element = undefined, className = '', override = {}, label = 'label', description = null, error = null, required = false, labelProps = {}, descriptionProps = {}, errorProps = {}, id = 'input-id', labelElement = 'label', size = 'sm';
export { className as class };
let _labelProps;
$: {
    _labelProps = labelElement === 'label' ? { htmlFor: id, ...labelProps } : { ...labelProps };
}
$: ({ cx, classes, getStyles } = useStyles({ size }, { name: 'InputWrapper' }));
</script>

<Box
	bind:element
	{use}
	class={cx(className, classes.root, getStyles({ css: override }))}
	{...$$restProps}
>
	{#if label}
		<LabelElement class={classes.label} {..._labelProps} {label} {id} {labelElement} {required} />
	{/if}
	{#if description}
		<Text {...descriptionProps} color="gray" class={classes.description}>
			{description}
		</Text>
	{/if}
	<slot />
	{#if typeof error !== 'boolean' && error}
		<Text {...errorProps} {size} class={classes.error}>
			{error}
		</Text>
	{/if}
</Box>
