<script>import { onMount } from 'svelte';
import { Box } from '../Box';
import useStyles from './MediaQuery.styles';
export let use = [], element = undefined, className = '', override = {}, smallerThan = undefined, largerThan = undefined, styles = undefined, query = undefined;
export { className as class };
$: ({ cx, classes, getStyles } = useStyles({ query, styles, largerThan, smallerThan }, { name: 'MediaQuery' }));
function styleChildElement() {
    element.children[0].classList.add(getStyles().selector);
}
onMount(styleChildElement);
</script>

<Box {use} bind:element class={cx(className, classes.root, getStyles({ css: override }))}>
	<slot />
</Box>
