<script>import { createEventDispatcher } from 'svelte';
import { CloseButton } from '../ActionIcon';
import { Box } from '../Box';
import IconRenderer from '../IconRenderer/IconRenderer.svelte';
import { Loader } from '../Loader';
import { Text } from '../Text';
import useStyles from './Notification.styles';
export let use = [], element = undefined, className = 'blue', override = {}, title = undefined, color = 'blue', radius = 'sm', loading = false, icon = undefined, iconSize = 16, iconProps = {}, withCloseButton = true, closeButtonLabel = undefined, closeButtonProps = {};
export { className as class };
const dispatch = createEventDispatcher();
function onClose() {
    dispatch('close');
}
$: ({ cx, classes } = useStyles({ color, radius }, { override, name: 'Notification' }));
</script>

<Box
	{use}
	bind:element
	role="alert"
	class={cx(className, classes.root, {
		withIcon: icon,
		withLoader: loading
	})}
	{...$$restProps}
>
	{#if icon && !loading}
		<slot name="icon">
			<IconRenderer {icon} className={classes.icon} {...iconProps} />
		</slot>
	{/if}
	{#if loading}
		<Loader class={classes.loader} size={28} {color} />
	{/if}
	<div class={classes.content}>
		{#if title}
			<Text class={classes.title} size="sm" weight={500}>
				{title}
			</Text>
		{/if}
		<Text class={classes.description} size="sm" color="dimmed">
			<slot />
		</Text>
	</div>
	{#if withCloseButton}
		<CloseButton
			class={classes.closeButton}
			aria-label={closeButtonLabel}
			variant="hover"
			size={28}
			{iconSize}
			{...closeButtonProps}
			on:click={onClose}
		/>
	{/if}
</Box>
