<script>import useStyles from './Paper.styles';
import { Box } from '../Box';
export let use = [], element = undefined, className = '', override = {}, shadow = 'xs', radius = 'sm', withBorder = false, padding = 'md';
export { className as class };
$: ({ cx, classes, getStyles } = useStyles({ radius, shadow, withBorder, padding }, { name: 'Paper' }));
</script>

<Box
	bind:element
	class={cx(
		classes.root,
		className,
		withBorder && classes.withBorder,
		getStyles({ css: override })
	)}
	{use}
	{...$$restProps}
>
	<slot />
</Box>
