<script>import { createEventDispatcher } from 'svelte';
import InputWrapper from '../../InputWrapper/InputWrapper.svelte';
import Group from '../../Group/Group.svelte';
import Radio from '../Radio.svelte';
import { randomID } from '../../../styles';
export let use = [], element = undefined, className = '', override = {}, items = [], group = undefined, color = undefined, value = undefined, label = null, disabled = false, size = undefined, radius = undefined, position = 'left', direction = 'row', labelDirection = 'right', align = 'flex-start', spacing = 'md', name = randomID();
export { className as class };
const dispatch = createEventDispatcher();
function onChanged(val, el) {
    const checked = el.checked;
    value = checked ? val : undefined;
    dispatch('change', val);
}
$: group = group || value;
</script>

<!--
@component

A Radio group component is a container for Radios.

@see https://svelteui.org/core/Radio
@example
    ```svelte
    <RadioGroup bind:value items={items} />
    <RadioGroup label={"Choose your favorite framework"} description={"Choose carefuly"} bind:value={value} items={items} />
    <RadioGroup bind:value={value} items={items} direction={'column'} itemDirection={'right'}/>
    <RadioGroup bind:value items={[{label: 'One', value: 1}, {label: 'Two', value: 2}, {label: 'Three', value: 3}]} />
    ```
-->

<InputWrapper bind:element class={className} {label} {override} {size} {...$$restProps}>
	<Group {direction} {align} {position} {spacing}>
		{#if items && items.length > 0}
			{#each items as item}
				<Radio
					{use}
					bind:group
					label={item.label}
					value={item.value}
					{labelDirection}
					{radius}
					{size}
					{color}
					{name}
					{disabled}
					on:change={(e) => onChanged(item.value, e.target)}
				/>
			{/each}
		{:else}
			<slot />
		{/if}
	</Group>
</InputWrapper>
