<script>import { Box } from '../Box';
import useStyles from './Space.styles';
export let use = [], element = undefined, className = '', override = {}, w = 0, h = 0;
export { className as class };
$: ({ cx, classes, getStyles } = useStyles({ h, w }, { name: 'Space' }));
</script>

<!--
@component

Add horizontal or vertical spacing from theme.

@see https://svelteui.org/core/space
@example
    ```svelte
		<Space h="md" />
		<Space w="lg" />
		<Space w={30} /> // Width will be set to 30px
    ```
-->
<Box
	bind:element
	{use}
	class={cx(className, classes.root, getStyles({ css: override }))}
	{...$$restProps}
>
	<slot />
</Box>
