<script>import { get_current_component } from 'svelte/internal';
import { createEventForwarder, useActions } from '../../internal';
import { randomID } from '../../styles';
import useStyles from './Switch.styles';
export let use = [], element = undefined, className = '', override = {}, color = 'blue', size = 'sm', radius = 'xl', transitionFunction = 'linear', id = randomID(), label = '', onLabel = '', offLabel = '', disabled = false, checked = false;
export { className as class };
/** An action that forwards inner dom node events from parent component */
const forwardEvents = createEventForwarder(get_current_component());
$: ({ cx, classes, getStyles } = useStyles({
    color,
    offLabel,
    onLabel,
    radius,
    size,
    transitionFunction
}, { name: 'Switch' }));
</script>

<!--
@component

A user can use this component to enable/disable something, normally used for boolean values or for binary actions.

@see https://svelteui.org/core/switch
@example
    ```tsx
    <Switch/> // standard switch
    <Switch label="Lights" onLabel="ON" offLabel="OFF"/> // switch with labels
    ```
-->
<div class={cx(className, classes.root)}>
	<input
		bind:this={element}
		{id}
		{disabled}
		use:useActions={use}
		use:forwardEvents
		bind:checked
		type="checkbox"
		class={cx(className, classes.input, getStyles({ css: override }))}
		class:disabled
	/>
	{#if label}
		<label for={id} class={classes.label}>
			{label}
		</label>
	{/if}
</div>

<style>
	.switch {
		display: flex;
		flex-direction: row;
		align-items: center;
	}
	.label {
		padding-left: 10px;
	}
</style>
