<script>import { get_current_component } from 'svelte/internal';
import { createEventForwarder, useActions } from '../../internal';
import { randomID } from '../../styles';
import { Input } from '../Input';
import { InputWrapper } from '../InputWrapper';
export let use = [], element = undefined, className = '', override = {}, label = '', description = null, error = null, required = false, labelProps = {}, descriptionProps = {}, errorProps = {}, invalid = false, id = 'input-id', labelElement = 'label', size = 'sm', showRightSection = undefined, value = '', placeholder = '';
export { className as class };
/** An action that forwards inner dom node events from parent component */
const forwardEvents = createEventForwarder(get_current_component());
const baseId = randomID(id);
// Flag that enables the override of the right section slot
// of the Input component only if it was provided
const _showRightSection = showRightSection === undefined ? !!$$slots.rightSection : showRightSection;
$: _invalid = invalid || !!error;
</script>

<!--
@component

Input for text that also uses labels for the input

@see https://svelteui.org/core/text
@example
    ```tsx
    <TextInput
        placeholder='Your name'
        description="Don't forget to add your full name"
        label='Full name'
        required
    />
    <TextInput
        placeholder='Your name'
        label='Full name'
        size='lg'
        on:change={onChange}
    />
    ```
-->

<InputWrapper
	bind:element
	class={className}
	{override}
	{label}
	{description}
	{error}
	{required}
	{labelProps}
	{descriptionProps}
	{errorProps}
	id={baseId}
	{labelElement}
	{size}
>
	<Input
		bind:value
		{required}
		{size}
		id={baseId}
		{placeholder}
		{...$$restProps}
		use={[forwardEvents, [useActions, use]]}
		invalid={_invalid}
		showRightSection={_showRightSection}
	>
		<slot slot="rightSection" name="rightSection" />
	</Input>
</InputWrapper>
